package array_ArrayList;

public class Array12 {
//Java Program to print the sum of all the items of the array
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int arr[]= {1,2,3,4,5};
		int sum=0;
		for(int i:arr)
		{
			sum=sum+i;
		}
		System.out.println(sum);
	}

}
