package string;

public class EqualsEgnoreCase {
	
	public static void equalsEgnoreCaseMethod(String str,String str2)
	{
		System.out.println(str.equalsIgnoreCase(str2));
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		equalsEgnoreCaseMethod("Anil","anil");
	}

}
